import { TextField, Button, Paper, Alert } from "@mui/material";
import React, { useState } from "react";


const Login: React.FC = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const submitValue = async () => {
    // setMessage(email);
    // setMessage(password);
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email_id: email, password })
    };
    let data: any = await fetch('http://localhost:4000/login', requestOptions)
    data = await data.json()
    console.log(data.msg)
    setMessage(data.msg)
  }

  const showCred: Function = () => {
    console.log(message, 'mess')

    if (message === 'Success') {
      return <Alert variant="outlined" sx={{ mb: 2 }} severity="success">Success.</Alert>
    }
    else if (message === 'user not found') {
      return <Alert variant="outlined" sx={{ mb: 2 }} severity="warning">User Not Found.</Alert>
    }
    else if (message === 'invalid password') {
      return <Alert variant="outlined" sx={{ mb: 2 }} severity="error">Invalid Password.</Alert>
    }
    else if (message) {
      return <Alert variant="outlined" sx={{ mb: 2 }} severity="error">Internal Server Error.</Alert>
    }
    else {
      return ""
    }
  }

  const handleEmailHelper: any = () => {
    if (!email) return "Email Field is Empty"
    else if (email.length < 12) return "Email is Not Valid"
  }
  const handleEmailError: any = () => {
    if (!email) return true
    else if (email.length < 12) return true
    return false
  }
  const handlePassHelper: any = () => {
    if (!password) return "Password Field is Empty"
    else if (password.length < 5) return "Password is Not Valid"
  }
  const handlePassError: any = () => {
    if (!password) return true
    else if (password.length < 5) return true
    return false
  }

  const myStyle: object = {
    height: "auto",
    width: "auto",
    padding: "0rem 4rem 0rem 4rem",
    backgroundColor: "white",
    // backgroundColor: "#c2bbc2",
    // color: "red",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    borderRadius: "1rem",
  };
  return (
    <Paper style={myStyle} >
      <h2 style={{ color: "rgb(62 48 58)", textAlign: "center" }}> LOG - IN </h2>
      < TextField
        id="outlined"
        label="ENTER EMAIL ID"
        variant="outlined"
        required
        error={handleEmailError()}
        helperText={handleEmailHelper()}
        name="email"
        type="email"
        autoComplete="email"
        // autoFocus
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      < TextField
        id="basic"
        label="ENTER PASSWORD"
        variant="outlined"
        required
        error={handlePassError()}
        helperText={handlePassHelper()}
        name="password"
        type="password"
        autoComplete="current-password"
        sx={{ mt: 2 }}
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      < Button
        type="submit"
        variant="contained"
        sx={{ mt: 2, mb: 2 }}
        onClick={submitValue}
        // onClick={() => {
        //   alert(`email is ${email} ans password is ${password}`);
        // }}
        color="secondary"
      >
        Login
      </Button>
      {/* {arr.length > 0 ? <h2>your Email is {email} and password is {password}</h2> :<div>Hello</div>} */}

      {showCred()}

    </Paper>

  );
};

export default Login;
