import { TextField, Button, Paper, Alert,InputLabel } from "@mui/material";
import React, { useState } from "react";


const Login: React.FC = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const submitValue = async () => {
    // setMessage(email);
    // setMessage(password);
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email_id: email, password: password })
    };
    let data: any = await fetch('http://localhost:4000/login', requestOptions)
    data = await data.json()
    console.log(data.msg)
    setMessage(data.msg)

  }
  const showCred: Function = () => {
    console.log(message, 'mess')
    if (message === 'Success') {
      return <Alert variant="outlined" sx={{ mb: 2 }} severity="success">Success.</Alert>
    }
    else if (message === 'user not found') {
      return <Alert variant="outlined" sx={{ mb: 2 }} severity="warning">User Not Found.</Alert>
    }
    else if (message === 'invalid password') {
      return <Alert variant="outlined" sx={{ mb: 2 }} severity="error">Invalid Password.</Alert>
    }
    else if (message) {
      return <Alert variant="outlined" sx={{ mb: 2 }} severity="error">Internal Server Error.</Alert>
    }
    else {
      return ""
    }
  }
  const myStyle: object = {
    height: "auto",
    width: "20rem",
    backgroundColor: "white",
    // backgroundColor: "#c2bbc2",
    // color: "red",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    borderRadius: "1rem",
  };
  return (
    <Paper style={myStyle} >
      <h2 style={{ color: "rgb(62 48 58)", textAlign: "center" }}> LOG - IN </h2>
      < TextField
        id="outlined"
        label="ENTER EMAIL ID"
        variant="outlined"
        required
        name="email"
        type="email"
        autoComplete="email"
        autoFocus
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <InputLabel htmlFor="outlined-adornment-password">Password</InputLabel>
          <OutlinedInput
            id="outlined-adornment-password"
            type={showPassword ? 'text' : 'password'}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {showPassword ? <VisibilityOff /> : <Visibility />}
                </IconButton>
              </InputAdornment>
            }
            label="Password"
          />
      < Button
        type="submit"
        variant="contained"
        sx={{ mt: 2, mb: 2 }}
        onClick={submitValue}
        // onClick={() => {
        //   alert(`email is ${email} ans password is ${password}`);
        // }}
        color="secondary"
      >
        Login
      </Button>
      {/* {arr.length > 0 ? <h2>your Email is {email} and password is {password}</h2> :<div>Hello</div>} */}

      {showCred()}

    </Paper>

  );
};

export default Login;
